class Solution:
    def timeTaken(self, edges: List[List[int]]) -> List[int]:
        n = len(edges) + 1
        g = [[] for x in range(n)]
        for u,v in edges:
            g[u].append(v)
            g[v].append(u)

        ans = [0] * n
        f = [0] * n
        def dfs(u: int,fa: int):
            for v in g[u]:
                if v == fa:continue
                dfs(v,u)
                ans[u] = max(ans[v] + (1 if (v & 1) else 2),ans[u])
        dfs(0,-1)
        # print(ans)
        def dfs1(u: int,fa: int):
            mx,se = 0,0
            for v in g[u]:
                if v == fa:continue
                if ans[v] + (1 if (v & 1) else 2) > mx:
                    se = mx
                    mx = ans[v] + (1 if (v & 1) else 2)
                elif ans[v] + (1 if (v & 1) else 2) > se:se = ans[v] + (1 if (v & 1) else 2)
            if f[u] > mx:
                se = mx
                mx = f[u]
            elif f[u] > se:se = f[u]
            # print(u,mx,se,dp[u])
            for v in g[u]:
                if v == fa:continue
                # print(u,v,ans[v],mx,se,dp[u])
                if ans[v] + (1 if (v & 1) else 2) <= se:
                    ans[v] = max(ans[v],mx + (1 if (u & 1) else 2))
                    f[v] = mx + (1 if (u & 1) else 2)
                else:
                    ans[v] = max(ans[v],se + (1 if (u & 1) else 2))
                    f[v] = se + (1 if (u & 1) else 2)
                dfs1(v,u)
        dfs1(0,-1)
        return ans
