class Solution {
public:
    vector<int> timeTaken(vector<vector<int>>& e) {
        int n = e.size() + 1;
        vector<vector<int>> adj(n);
        for (int i = 0; i < n - 1; i++) {
            int a = e[i][0], b = e[i][1];
            adj[a].push_back(b);
            adj[b].push_back(a);
        }
        vector<int> dp(n), dp2(n);
        function<void(int, int)> dfs = [&](int x, int p) {
            for (auto y : adj[x]) {
                if (y == p) continue;
                dfs(y, x);
                dp[x] = max(dp[x], dp[y] + (y % 2 ? 1 : 2));
            }
        };
        dfs(0, -1);
        vector<int> ans(n);
        function<void(int, int)> rer = [&](int x, int p) {
            ans[x] = max(dp[x], dp2[x]);
            multiset<int> s;
            s.insert(-1e9);
            for (auto y : adj[x]) if (y != p) s.insert(dp[y] + (y%2?1:2));
            for (auto y : adj[x]) {
                if (y == p) continue;
                s.erase(s.find(dp[y] + (y%2?1:2)));
                dp2[y] = max(*s.rbegin(), dp2[x]) + (x%2?1:2);
                rer(y, x);
                s.insert(dp[y] + (y%2?1:2));
            }
        };
        rer(0, -1);
        return ans;
    }
};
