class Solution {
public:
    int n;
    vector<int> t,ans;
    vector<vector<int>> g;
    vector<vector<int>> t2;
    int recur(int x,int p){
        t2[x]={0,0,0};
        for(auto &i:g[x]){
            if(i==p)continue;
            t2[x][2]=recur(i,x)+(i%2?1:2);
            sort(t2[x].begin(),t2[x].end(),greater<int>());
        }
        return t[x]=(t2[x][0]!=-1?t2[x][0]:0); 
    }
    void setans(int x,int p){
        if(p!=-1){
            t2[x][2]=( (t2[p][0] == t[x]+(x%2?1:2)) ?t2[p][1]:t2[p][0])
         + (p%2?1:2);
            sort(t2[x].begin(),t2[x].end(),greater<int>());
        }
        ans[x]=(t2[x][0]!=-1?t2[x][0]:0);
        // cout << t2[x][0] << " "  << t2[x][1] << " "  << t2[x][2] << "\n";
        // if(p!=-1)cout << "final " << x << " " << ans[x] << " from " << ( (t2[p][0] == t[x]+(x%2?1:2)) ?t2[p][1]:t2[p][0]) << " " << endl;
        for(auto &i:g[x]){
            if(i==p)continue;
            setans(i,x);
        }
    }
    vector<int> timeTaken(vector<vector<int>>& edges) {
        n=edges.size()+1;
        ans=vector<int>(n);
        t=vector<int>(n);
        g=vector<vector<int>> (n);
        t2=vector<vector<int>> (n,vector<int>(3));
        for(auto &i:edges){
            g[i[1]].push_back(i[0]);
            g[i[0]].push_back(i[1]);
        }
        recur(0,-1);
        setans(0,-1);
        return ans;
    }
};
