#define lb lower_bound
#define ub upper_bound
#define sf(i) sizeof(i)
#define endl "\n"
#define sz(v) ((int)(v).size())
#define all(v) (v).begin(), (v).end()
#define rep(i, L, R) for(ll i = L;i<=R;i++)
#define pcis precision


class Solution {
public:

    vl adj[100001];
ll LST[100001];
ll DOWN[100001];
bool vst[100001];
ll DFS(ll node) {
    vst[node] = true;
    ll MX = 0;
    for (auto i : adj[node]) {
        if (vst[i] == false) MX = max(MX, DFS(i) + (i%2==0?2:1));
    }
    DOWN[node] = MX;
    return MX;
}

void DFS2(ll node, ll up) {
    // cout << node << " : " << up << endl;
    vst[node] = true;
    ll MX = up;
    ll MXidx = 0;
    ll secMX = 0;
    LST[node] = max(DOWN[node], up);
    for (auto i : adj[node]) {
        if (vst[i] == false) {
            ll dw = DOWN[i] + (i%2==0?2:1);
            if (dw > MX) {
                secMX = MX;
                MXidx = i;
                MX = dw;
            }else if (secMX < dw) {
                secMX = dw;
            }
        }
    }
    for (auto i : adj[node]) {
        if (vst[i] == false) {
            ll cot = (i==MXidx?secMX:MX);
            DFS2(i, cot+(node%2==0?2:1));
        }
    }
    return;
}
    vector<int> timeTaken(vector<vector<int>>& edges) {

        ll N =edges.size() + 1;
        for (ll i = 0;i<N-1;i++) {
            ll a = edges[i][0];
            ll b = edges[i][1];
            adj[a].pb(b);
            adj[b].pb(a);
        }

        DFS(0);
        memset(vst, 0 ,sf(vst));
        DFS2(0,0);
        vi lst;
        for (ll i = 0;i<N;i++) {
            lst.pb(LST[i]);
        }
        return lst;


    }
};
