class Solution {
public:
    void dfs(int node,int par,vector<int> adj[],vector<int>& res){
        int maxi=0;
        for(auto &nei:adj[node]){
            if(nei==par)continue;
            dfs(nei,node,adj,res);
            maxi=max(maxi,res[nei]);
        }
        res[node]=maxi+((node&1)?1:2);
    }
    void dfs2(int node,int par,vector<int> adj[],vector<int>& res,int parentCont){
        int maxi1=0,maxi2=0;
        for(auto &nei:adj[node]){
            if(nei==par)continue;
            if(res[nei]>maxi1){
                maxi2=maxi1;
                maxi1=res[nei];
            }else if(res[nei]>maxi2){
                maxi2=res[nei];
            }
        }
        int curVal=((node&1)?1:2);
        res[node]=max(res[node],curVal+parentCont);
        for(auto &nei:adj[node]){
            if(nei==par)continue;
            if(res[nei]==maxi1){
                dfs2(nei,node,adj,res,curVal+max(parentCont,maxi2));
            }else{
                dfs2(nei,node,adj,res,curVal+max(parentCont,maxi1));
            }
        }
    }
    vector<int> timeTaken(vector<vector<int>>& edges) {
        // Contribution from parent
        int n=edges.size()+1;
        vector<int> adj[n];
        for(auto &e:edges){
            adj[e[0]].push_back(e[1]);
            adj[e[1]].push_back(e[0]);
        }
        vector<int> res(n);
        dfs(0,-1,adj,res);
        dfs2(0,-1,adj,res,0);
        for(int i=0;i<n;++i){
            int dec=(i&1)?1:2;
            res[i]-=dec;
        }
        return res;
    }
};
