class Solution {
  public:
   vector<int> timeTaken(vector<vector<int>>& edges) {
      int n = edges.size() + 1;
      vector<vector<int>> adj(n);
      for (auto a : edges) {
         int u = a[0], v = a[1];
         adj[u].emplace_back(v);
         adj[v].emplace_back(u);
      }
      const int inf = 1e9;
      vector<pair<int, int>> f(n);
      auto dfs = [&](auto&& self, int v, int p) -> void {
         for (auto u : adj[v]) {
            if (u == p) continue;
            self(self, u, v);
            int ad = 1 + (u % 2 == 0);
            if (f[u].first + ad > f[v].second) f[v].second = f[u].first + ad;
            if (f[v].first < f[v].second) swap(f[v].first, f[v].second);
         }
      };
      dfs(dfs, 0, -1);
      vector<int> ans(n);
      auto rero = [&](auto&& self, int v, int p, int pa) -> void {
         ans[v] = max(pa, f[v].first);
         for (auto u : adj[v]) {
            if (u == p) continue;
            int gv = (f[u].first + 1 + (u % 2 == 0) == f[v].first ? f[v].second : f[v].first);
            gv = max(gv, pa);
            gv += 1 + (v % 2 == 0);
            self(self, u, v, gv);
         }
      };
      rero(rero,0,-1,0);
      return ans;
   }
};
