class Solution {
public:
    void dfs(int u,vector<int>&dis,vector<vector<int>>&adj){
        for(auto it:adj[u]){
            if(dis[it]==-1) {
                if(it%2==1)dis[it]=dis[u]+1;
                if(it%2==0) dis[it]=dis[u]+2;
                dfs(it,dis,adj);
            }
        }
    }
    vector<int> timeTaken(vector<vector<int>>& edges) {
        int n=edges.size()+1;
        vector<vector<int>>adj(n);
        for(auto it:edges){
            adj[it[0]].push_back(it[1]);
            adj[it[1]].push_back(it[0]);
        }
        vector<int>dis(n,-1);dis[0]=0;
        dfs(0,dis,adj);int idx=0;
        for(int i=0;i<n;i++){
            if(dis[i]>dis[idx]) idx=i;
        }
        int t,t1;
        //cout<<idx<<endl;
        t=idx;
        vector<int>dis1(n,-1);dis1[idx]=0;
        dfs(idx,dis1,adj);idx=0;
        for(int i=0;i<n;i++){
            if(dis1[i]>dis1[idx]) idx=i;
        }
        //cout<<idx<<endl;
        t1=idx;
        vector<int>dis2(n,-1);dis2[idx]=0;
        dfs(idx,dis2,adj);
        vector<int>ans(n,0);
        for(int i=0;i<n;i++){
            //cout<<dis1[i]<<" "<<dis2[i]<<endl;

            if(t%2==0 && i%2 && t!=i) dis1[i]++;
            if(t1%2==0 && i%2  && t1!=i) dis2[i]++;
            if(i%2==0 && t%2) dis1[i]--;
            if(i%2==0 && t1%2) dis2[i]--; 

            ans[i]=max(dis1[i],dis2[i]);
        }
        return ans;
    }
};
