const int N= 1e5+5;
vector<int> graph[N];
vector<int> dis1, dis2, dis;
class Solution {
public:
    void dfs(int vertex, int par){
        for(auto x: graph[vertex]){
            if(x!=par){
                if(x%2){
                    dis[x]= dis[vertex]+1;
                }
                else{
                    dis[x]= dis[vertex]+2;
                }
                dfs(x,vertex);
            }
        }
    }
    vector<int> timeTaken(vector<vector<int>>& edges) {
        int n= edges.size()+1;
        for(int i=0;i<=n;i++){
            graph[i].clear();
        }
        for(auto v: edges){
            graph[v[0]].push_back(v[1]);
            graph[v[1]].push_back(v[0]);
        }
        dis= vector<int> (n+1);
        dis[0]=2;
        dfs(0,-1);
        int r1=0, d=0;
        for(int i=0;i<n;i++){
            if(dis[i]>d){
                r1=i;
                d=dis[i];
            }
        }
        dis[r1]=(r1%2);
        if(r1%2==0){
            dis[r1]=2;
        }
        dfs(r1,-1);
        dis1= dis;
        int r2=0;d=0;
        for(int i=0;i<n;i++){
            if(dis[i]>d){
                r2=i;
                d=dis[i];
            }
        }
        dis[r2]=1;
        if(r2%2==0){
            dis[r2]=2;
        }
        dfs(r2,-1);
        dis2=dis;
        vector<int> ans(n);
        for(int i=0;i<n;i++){
            int d1= dis1[i], d2= dis2[i];
            ans[i]= max(d1,d2)-(i%2);
            if(i%2==0){
                ans[i]-=2;
            }
        }
        return ans;
    }
};
