class Solution {
public:
    vector<int> timeTaken(vector<vector<int>>& edges) {
        int n=(int)edges.size()+1;
        multiset<int> s[n];


        vector<vector<int>> g(n);

        for(auto x:edges){
            g[x[0]].push_back(x[1]);
            g[x[1]].push_back(x[0]);
        }



        auto dfs=[&](auto&&dfs,int i,int p)->void{
            s[i].insert(0);
            for(auto j:g[i]){
                if(j==p) continue;
                dfs(dfs,j,i);
                s[i].insert(*s[j].rbegin()+1+(j%2==0));
            }
        };


        dfs(dfs,0,-1);
        vector<int> ans(n,0);

        auto dfs1=[&](auto&&dfs1,int i,int p)->void{
            ans[i]=*s[i].rbegin();
            for(auto j:g[i]){
                if(j==p) continue;
                int contri=*s[j].rbegin()+1+(j%2==0);
                s[i].erase(s[i].find(contri));
                s[j].insert(*s[i].rbegin()+1+(i%2==0));
                s[i].insert(contri);
                dfs1(dfs1,j,i);
            }
        };

        dfs1(dfs1,0,-1);
        return ans;

    }
};
