class Solution {
public:
    vector<int> g[100010];
    int f[100010],F[100010],k[100010];

    void dfs(int u,int from){
        f[u]=0;
        for(unsigned i=0;i<g[u].size();i++){
            int v=g[u][i];
            if(v==from)continue;
            dfs(v,u);
            f[u]=max(f[u],f[v]+k[v]);
        }
        return;
    }

    void dfs2(int u,int from,int p){
        F[u]=max(f[u],p);
        int mx=p,mxp=-1,se=0;
        for(unsigned i=0;i<g[u].size();i++){
            int v=g[u][i];
            if(v==from)continue;
            if(f[v]+k[v]>mx)se=mx,mx=f[v]+k[v],mxp=v;
            else if(f[v]+k[v]>se)se=f[v]+k[v];
        }
        mx+=k[u],se+=k[u];
        for(unsigned i=0;i<g[u].size();i++){
            int v=g[u][i];
            if(v==from)continue;
            if(v!=mxp)dfs2(v,u,mx);
            else dfs2(v,u,se);
        }
        return;
    }

    vector<int> timeTaken(vector<vector<int>>& edges) {
        vector<int> ans;
        int n=edges.size()+1;
        for(int i=0;i<n;i++)g[i].resize(0),k[i]=((i&1)?1:2);
        for(int i=0;i<n-1;i++){
            int u=edges[i][0],v=edges[i][1];
            g[u].push_back(v);
            g[v].push_back(u);
        }
        dfs(0,-1);
        dfs2(0,-1,0);
        for(int i=0;i<n;i++)ans.push_back(F[i]);
        return ans;
    }
};
