class Solution {
public:
    vector<multiset<int>>score;
    vector<int>ans;
    int dfs(int node,vector<int>adj[],int par){
        int res=1;
        if(node%2==0)res++;
        for(auto j:adj[node]){
            if(j!=par){
                int temp=dfs(j,adj,node);
                score[node].insert(temp+res);
            }
        }
        if(score[node].empty())score[node].insert(res);
        return *score[node].rbegin();
    }
    void dfs2(int node,vector<int>adj[],int par){
        int res=1;
        if(node%2==0)res++;
        ans[node]=(*score[node].rbegin())-res;
        for(auto j:adj[node]){
            if(j!=par){
                score[node].erase(score[node].find((*score[j].rbegin())+res));
                if(score[node].empty())score[node].insert(res);
                int child=1;
                if(j%2==0)child++;
                score[j].insert((*score[node].rbegin())+child);
                dfs2(j,adj,node);
                score[j].erase(score[j].find((*score[node].rbegin())+child));
                if(score[j].empty())score[j].insert(child);
                score[node].insert((*score[j].rbegin())+res);
            }
        }
        return ;
    }
    vector<int> timeTaken(vector<vector<int>>& edges) {
        score.resize(edges.size()+1);
        ans.resize(edges.size()+1,0);
        vector<int>adj[edges.size()+1];
        for(auto j:edges){
            adj[j[0]].push_back(j[1]);
            adj[j[1]].push_back(j[0]);
        }
        dfs(0,adj,-1);
        dfs2(0,adj,-1);
        return ans;
    }
};
