class Solution {
public:
    void fn(vector<ll> graph[],ll node,ll par,ll curr,ll &dis,ll &x){
        if(curr>dis){
            dis=curr;
            x=node;
        }
        for(auto &nbr:graph[node]){
            if(nbr==par)continue;
            fn(graph,nbr,node,curr+((nbr&1)?1:2),dis,x);
        }
    }
    void getdis(vector<ll> graph[],ll node,ll par,ll curr,v64 &dis){
        dis[node]=curr;
        for(auto &nbr:graph[node]){
            if(nbr==par)continue;
            getdis(graph,nbr,node,curr+((node&1)?1:2),dis);
        }
    }
    vector<int> timeTaken(vector<vector<int>>& e) {
        ll n=sz(e)+1;
        vector<ll> graph[n];
        for(auto &ed:e){
            graph[ed[0]].pb(ed[1]);
            graph[ed[1]].pb(ed[0]);
        }
        v32 ans(n,0);
        ll x=-1,y=-1;
        ll dx=0,dy=0;
        fn(graph,0,-1,0,dx,x);
        fn(graph,x,-1,0,dy,y);
        // dbg(x);
        // dbg(y);
        v64 d1(n,0);
        v64 d2(n,0);
        getdis(graph,x,-1,0,d1);
        getdis(graph,y,-1,0,d2);
        for(ll i=0;i<n;i++){
            ans[i]=max(d1[i],d2[i]);
        }
        return ans;
    }
};
