class Solution {
private:
    using Graph = vector<vector<int>>;

    int getCost(int a, int b){
        return 1 + (b + 1) % 2;
    }

    void dfs(int parent, int node, const Graph& G, vector<vector<pair<int, int>>>& v){
        int childrenCount = 0;
        for(int nextNode: G[node]){
            if(nextNode != parent){
                childrenCount += 1;
                dfs(node, nextNode, G, v);
                v[node].push_back({getCost(node, nextNode) + v[nextNode][0].first, nextNode});
                sort(v[node].rbegin(), v[node].rend());
                v[node].resize(2);
            }
        }
        if(childrenCount == 0){
            v[node].resize(2);
        }
    }

    void reroot(int parent, int node, const Graph& G, vector<vector<pair<int, int>>>& v, vector<int>& res){
        if(v[parent][0].second == node){
            v[node].push_back({getCost(node, parent) + v[parent][1].first, parent});
        }else{
            v[node].push_back({getCost(node, parent) + v[parent][0].first, parent});
        }

        sort(v[node].rbegin(), v[node].rend());

        res[node] = v[node][0].first;

        for(int nextNode: G[node]){
            if(nextNode != parent){
                reroot(node, nextNode, G, v, res);
            }
        }
    }

public:
    vector<int> timeTaken(vector<vector<int>>& edges) {
        const int E = edges.size();
        const int N = E + 1;

        Graph G(N);
        for(const vector<int>& EDGE: edges){
            int a = EDGE[0];
            int b = EDGE[1];
            G[a].push_back(b);
            G[b].push_back(a);
        }


        vector<vector<pair<int, int>>> v(N);
        dfs(-1, 0, G, v);

        vector<int> res(N);
        res[0] = v[0][0].first;

        for(int child: G[0]){
            reroot(0, child, G, v, res);
        }

        return res;
    }
};
