 /*ॐ भूर्भुवः स्व तत्सवितुर्वरेण्यं 
भर्गो देवस्य धीमहि
धियो यो नः प्रचोदयात॥*/

#include <iostream>
#include <algorithm>
#include <bits/stdc++.h>
#include <ext/pb_ds/assoc_container.hpp>
#include <ext/pb_ds/tree_policy.hpp>
#define ll long long int
#define vi vector<ll>
#define vec vector<int>
#define vs vector<string>
#define pb push_back
#define forll for (ll i = 0; i < n; i++)
#define forf(i, a, b) for (ll i = a; i < b; i++)
#define forr(i, x, y) for (ll i = x; i >= y; i--)
#define mod 1000000007
// #define MOD 998244353
using namespace std;
using namespace __gnu_pbds;
template <class T>
using ordered_set = tree<T, null_type, less<T>, rb_tree_tag, tree_order_statistics_node_update>;


class Solution {
public:
    void dfs(ll node,ll par,vi adj[],vector<pair<ll,ll>> &maxi,vi &subtree){
        ll mx1=0,mx2=0;
        for(auto &child:adj[node]){
            if(child!=par){
                dfs(child,node,adj,maxi,subtree);
                ll childVal=((child%2)?1:2)+subtree[child];
                subtree[node]=max(subtree[node],childVal);
                if(childVal>=mx1){
                    mx2=mx1;
                    mx1=childVal;
                }
                else if(childVal>=mx2){
                    mx2=childVal;
                }
            }
        }
        maxi[node].first=mx1,maxi[node].second=mx2;
    }
    void reroot(ll node,ll par,vi adj[],vector<pair<ll,ll>> &maxi,vi &subtree,vi &out){
        for(auto &child:adj[node]){
            if(child!=par){
                ll maxVal=maxi[node].first;
                if(maxVal==subtree[child]+((child%2)?1:2)){
                    maxVal=maxi[node].second;
                }
                maxVal+=((node%2)?1:2);
                out[child]=max(maxVal,out[node]+((node%2)?1:2));
                reroot(child,node,adj,maxi,subtree,out);
            }
        }
    }
    vector<int> timeTaken(vector<vector<int>>& edges) {
        ll n=edges.size()+1;
        vector<pair<ll,ll>> maxi(n+1);
        vi adj[n+1];
        vi subtree(n+1,0);
        vi out(n+1,0);
        for(auto &it:edges){
            adj[it[0]].push_back(it[1]);
            adj[it[1]].push_back(it[0]);
        }
        dfs(0,-1,adj,maxi,subtree);
        reroot(0,-1,adj,maxi,subtree,out);

        vector<int> time;
        forll{
            time.push_back(max({maxi[i].first,maxi[i].second,out[i],subtree[i]}));
        }
        return time;
    }
};
