class Solution {
public:
    void dfs2(int i,int p,int m,vector<vector<int>>& v,vector<int>& t,vector<int>& ans){ 
        int m1=m,m2=0;
        for(int j:v[i]){
            if(j!=p){
                if(t[j]+(j%2?1:2)>m1){
                m2=m1;
                m1=t[j]+(j%2?1:2);
            }
            else if(t[j]+(j%2?1:2)>m2) m2=t[j]+(j%2?1:2);}
        }
        ans[i]=m1;

        int x=0;
        if(i%2) x++;
        else x+=2;
        for(int j:v[i]){
           if(j!=p){
               if(t[j]+(j%2?1:2)!=m1){
                dfs2(j,i,m1+x,v,t,ans);
            }
            else {
                dfs2(j,i,m2+x,v,t,ans);
            }
           }
        }
        return ;
    }
    int dfs(int i,int p,vector<vector<int>>& v,vector<int>& t){
        int ans=0;
        for(int j:v[i]){
            if(j!=p){
                int x=dfs(j,i,v,t);
                if(j%2) ans=max(ans,x+1);
                else ans=max(ans,x+2);
            }
        }
        return t[i]=ans;
    }
    vector<int> timeTaken(vector<vector<int>>& e) {
        int n=e.size()+1;
        vector<vector<int>> v(n);
        for(int i=0;i<n-1;i++){
            v[e[i][0]].push_back(e[i][1]);
            v[e[i][1]].push_back(e[i][0]);
        }
        vector<int> t(n),ans(n);
        dfs(0,0,v,t);
        dfs2(0,0,0,v,t,ans);
        return ans;
    }
};
