class Solution {
public:
    const int N=1e5;
    vector<int> timeTaken(vector<vector<int>>& edges) {
        int n=0;
        vector<vector<int>>adj(N);
        for(auto v:edges){
            adj[v[0]].push_back(v[1]);
            adj[v[1]].push_back(v[0]);
            n=max(n,max(v[0],v[1]));
        }
        n++;
        vector<int>dist(n,-1);
        queue<pair<int,int>>q;
        q.push(pair(0,0));
        while(!q.empty()){
            pair<int,int>curr=q.front();
            q.pop();
            dist[curr.first]=curr.second;
            for(int v:adj[curr.first]){
                if(dist[v]==-1){
                    if(v&1)q.push(pair(v,curr.second+1));
                    else q.push(pair(v,curr.second+2));
                }
            }
        }
        int mx=0,d1=0,d2=0;
        for(int i=0;i<n;i++)if(mx<dist[i]){
            mx=dist[i];
            d1=i;
        }
        dist.assign(n,-1);
        q.push(pair(d1,0));
        while(!q.empty()){
            pair<int,int>curr=q.front();
            q.pop();
            dist[curr.first]=curr.second;
            for(int v:adj[curr.first]){
                if(dist[v]==-1){
                    if(v&1)q.push(pair(v,curr.second+1));
                    else q.push(pair(v,curr.second+2));
                }
            }
        }
        mx=0;
        for(int i=0;i<n;i++)if(mx<dist[i]){
            mx=dist[i];
            d2=i;
        }
        vector<int>dist2(n,-1);
        q.push(pair(d2,0));
        while(!q.empty()){
            pair<int,int>curr=q.front();
            q.pop();
            dist2[curr.first]=curr.second;
            for(int v:adj[curr.first]){
                if(dist2[v]==-1){
                    if(v&1)q.push(pair(v,curr.second+1));
                    else q.push(pair(v,curr.second+2));
                }
            }
        }
        if(d1&1){
            for(int i=0;i<n;i++){
                if(i&1)continue;
                else dist[i]--;
            }
        }else{
            for(int i=0;i<n;i++){
                if(i&1)dist[i]++;
            }
        }
        if(d2&1){
            for(int i=0;i<n;i++){
                if(i&1)continue;
                else dist2[i]--;
            }
        }else{
            for(int i=0;i<n;i++){
                if(i&1)dist2[i]++;
            }
        }
        vector<int>ans(n);
        for(int i=0;i<n;i++)ans[i]=max(dist[i],dist2[i]);
        return ans;
    }
};
