class Solution {
public:
    vector<int> timeTaken(vector<vector<int>>& edges) {
        int n=edges.size()+1;
        vector<vector<int>> adj(n);
        for(auto &x: edges){
            adj[x[0]].push_back(x[1]); adj[x[1]].push_back(x[0]);
        }
        vector<int> nodeDep1(n);
        vector<int> nodeDep2(n);
        function<int(int,int)> dfs1=[&](int node, int par)->int{
            int maxDepth=1;
            if(node%2==0 and node!=0){    
                maxDepth++;
            }
            int h=0;
            for(auto &node1: adj[node]){
                if(node1==par) continue;
                h=max(h,dfs1(node1,node));
            }
            maxDepth+=h;
            return nodeDep1[node]=maxDepth;
        };
        dfs1(0,-1);
        function<void(int,int,int)> dfs2=[&](int node, int par, int pardep)->void{
            int addDep=(node%2==0)+1;
            if(node!=0) {
                nodeDep2[node]=max(nodeDep1[node]-addDep,pardep);
            }
            else{
                nodeDep2[node]=nodeDep1[node]-1;
            }   
            int max1=0; int max2=0;
            for(auto &node1: adj[node]){
                if(node1==par) continue;
                if(max1<nodeDep1[node1]){
                    max2=max1;
                    max1=nodeDep1[node1];
                }
                else{
                    max2=max(max2,nodeDep1[node1]);
                }
            }
            for(auto &node1: adj[node]){
                if(node1==par) continue;
                int newp=max(pardep+addDep,(nodeDep1[node1] == max1 ? max2 : max1) + addDep);
                dfs2(node1,node,newp);
            }
        };
        dfs2(0,-1,0);
        return nodeDep2;
    }

};
