class Solution {
public:
    vector<int> timeTaken(vector<vector<int>>& edges) 
    {
        int n=edges.size()+1;
        vector<vector<int>> edge(n),dis(n),mx(n,vector<int>(2,0));
        for(int i=0;i<edges.size();++i)
        {
            edge[edges[i][0]].push_back(edges[i][1]);
            edge[edges[i][1]].push_back(edges[i][0]);
            dis[edges[i][0]].push_back(-1);
            dis[edges[i][1]].push_back(-1);
        }
        vector<bool> vis(n,false);
        dfs(0,edge,dis,vis,mx);
        for(int i=0;i<n;++i)
            vis[i]=false;
        dn(0,edge,dis,vis,mx,0);
        vector<int> ans(n);
        for(int i=0;i<n;++i)
            ans[i]=mx[i][0];
        return ans;
    }
private:
    int dfs(int n, vector<vector<int>> &edge, vector<vector<int>> &dis, vector<bool> &vis, vector<vector<int>> &mx)
    {
        vis[n]=true;
        for(int i=0;i<edge[n].size();++i)
        {
            if(vis[edge[n][i]])
                continue;
            dis[n][i]=dfs(edge[n][i],edge,dis,vis,mx);
            if(mx[n][1]<dis[n][i])
                mx[n][1]=dis[n][i];
            if(mx[n][0]<mx[n][1])
                swap(mx[n][1],mx[n][0]);
        }
        if(n&1)
            return mx[n][0]+1;
        return mx[n][0]+2;
    }
    void dn(int n, vector<vector<int>> &edge, vector<vector<int>> &dis, vector<bool> &vis, vector<vector<int>> &mx, int r)
    {
        vis[n]=true;
        if(r>mx[n][1])
            mx[n][1]=r;
        if(mx[n][0]<mx[n][1])
            swap(mx[n][1],mx[n][0]);
        for(int i=0;i<edge[n].size();++i)
        {
            if(vis[edge[n][i]])
                continue;
            if(n&1)
            {
                if(dis[n][i]==mx[n][0])
                    dn(edge[n][i],edge,dis,vis,mx,mx[n][1]+1);
                else
                    dn(edge[n][i],edge,dis,vis,mx,mx[n][0]+1);
            }
            else
            {
                if(dis[n][i]==mx[n][0])
                    dn(edge[n][i],edge,dis,vis,mx,mx[n][1]+2);
                else
                    dn(edge[n][i],edge,dis,vis,mx,mx[n][0]+2);
            }
        }
    }
};
