class Solution {
public:
    vector<vector<int>>cnt;
    vector<int>ans;
      void dfs2(int node,int p ,vector<vector<int>>&adj,int up){
          ans[node]=max(up,max(cnt[node][0],cnt[node][1]));
             for(auto it : adj[node]){
            if(it==p)continue;
            int t1=0;
                 int cost;
                 if(node%2==0)cost=2;
                 else cost=1;

            if(it%2==0){
              if(cnt[node][0]==cnt[it][0]+2)
              dfs2(it,node,adj,max(up,cnt[node][1])+cost);  
               else{
                dfs2(it,node,adj,max(up,cnt[node][0])+cost);  
               } 
            }
            else{
             if(cnt[node][0]==cnt[it][0]+1)
              dfs2(it,node,adj,max(up,cnt[node][1])+cost);  
               else{
                dfs2(it,node,adj,max(up,cnt[node][0])+cost);  
               }            }
               }
           return ;

      }
    int dfs(int node,int p, vector<vector<int>>&adj,int t){
         int x=0;
        int y=0;
        // cout<<node<<endl;
        for(auto it : adj[node]){
            // cout<<it<<endl;
            if(it==p)continue;
            int t1=0;
            if(it%2==0){
                t1 = 2+dfs(it,node,adj,t+2);     
            }
            else{
                t1 = 1+dfs(it,node,adj,t+1);     
            }
            if(t1>=x){
                y=x;
                x=t1;
            }
            else if(t1>y){
                y=t1;
            }
        }
    cnt[node]={x,y};
    return max(x,y);

    }
    vector<int> timeTaken(vector<vector<int>>& edges) {
        int n = edges.size()+1;
        cnt.resize(n+1);
        vector<vector<int>>adj(n+1);
        for(auto it :edges){
           adj[it[0]].push_back(it[1]);
           adj[it[1]].push_back(it[0]);

        }
        ans.resize(n);
        dfs(0,-1,adj,0);
        dfs2(0,-1,adj,0);
         // for(int i =0;i<n;i++){
         //     cout<<cnt[i][0]<<" "<<cnt[i][1]<<endl;
         // }

        return ans;

    }
};
