class Solution {
public:
    vector<int> ans;
    vector<unordered_map<int,int>> v;
    int path(int i, vector<vector<int>>& adj,vector<int>& trav){
        trav[i]=1;
        for(int x:adj[i]){
            if(!trav[x]){
                if(x%2) v[i][x]=1+path(x,adj,trav);
                else v[i][x]=2+path(x,adj,trav);
                ans[i]=max(v[i][x],ans[i]);
            }
        }
        return ans[i];
    }
    vector<int> timeTaken(vector<vector<int>>& edges) {
        int n=edges.size()+1;
        vector<vector<int>> adj(n);
        for(vector<int> edge:edges){
            adj[edge[0]].push_back(edge[1]);
            adj[edge[1]].push_back(edge[0]);
        }
        vector<int> trav(n,0);
        v.resize(n);
        ans.resize(n,0);
        int t=path(0,adj,trav);
        vector<int> ans_main(n,0);
        vector<vector<pair<int,int>>> max_two(n,vector<pair<int,int>>(2,{-1,0}));
        for(int i=0;i<n;i++){
            for(int j:adj[i]){
                if(v[i].find(j)!=v[i].end()){
                    if(v[i][j]>max_two[i][0].second) {
                        max_two[i][1]=max_two[i][0];
                        max_two[i][0]={j,v[i][j]};
                    }
                    else if(v[i][j]>max_two[i][1].second) max_two[i][1]={j,v[i][j]};
                }

            }
            // for(int j:adj[i]){
            //     if(v[i].find(j)!=v[i].end()){
            //         int val=0;
            //         if(i%2){
            //             if(max_two[i][0].first==j) ans[j]=max(ans[j],1+max_two[i][1].second);
            //             else ans[j]=max(ans[j],1+max_two[i][0].second);
            //         }
            //         else{
            //             if(max_two[i][0].first==j) ans[j]=max(ans[j],2+max_two[i][1].second);
            //             else ans[j]=max(ans[j],2+max_two[i][0].second);
            //         }
            //     }
            // }
            for(int j:adj[i]){
                if(v[i].find(j)!=v[i].end()){
                    int val=0;
                    if(i%2){
                        if(max_two[i][0].first==j) {
                            ans[j]=max(ans[j],1+max_two[i][1].second);
                            val=1+max_two[i][1].second;
                        }
                        else{
                            ans[j]=max(ans[j],1+max_two[i][0].second);
                            val=1+max_two[i][0].second;
                        }
                    }
                    else{
                        if(max_two[i][0].first==j) {
                            ans[j]=max(ans[j],2+max_two[i][1].second);
                            val=2+max_two[i][1].second;
                        }
                        else{
                            ans[j]=max(ans[j],2+max_two[i][0].second);
                            val=2+max_two[i][0].second;
                        }
                    }
                    if(val>max_two[j][0].second){
                        max_two[j][1]=max_two[i][0];
                        max_two[j][0]={i,val};
                    }
                    else if(val>max_two[j][1].second) max_two[j][1]={i,val};
                }
            }
        }
        return ans;

    }
