class Solution {
public:
    vector<int>time;
    int n;
    vector<vector<int>>g;
    vector<multiset<int>>v;
    int f(int node,int pa){
        int res=0;
        v[node].insert(0);
        for(auto&i:g[node]){
            if(i==pa)continue;
            int curr=f(i,node);
            int fac=i%2?1:2;
            v[node].insert(fac+curr);
            if(i%2)res=max(res,1+curr);
            else res=max(res,2+curr);
        }
        time[node]=res;
        return res;
    }
    void f1(int node,int pa){
        if(pa!=-1){
            int req=node%2?1:2;
            req+=time[node];
            v[pa].erase(v[pa].find(req));
            int fac=pa%2?1:2;
            v[node].insert(fac+*v[pa].rbegin());
            time[node]=*v[node].rbegin();
            v[pa].insert(req);
        }
        for(auto&i:g[node]){
            if(i==pa)continue;
            f1(i,node);
        }
    }
    vector<int> timeTaken(vector<vector<int>>& edges) {
        n=edges.size();
        v.assign(n+1,multiset<int>());
        g.assign(n+1,vector<int>());
        time.assign(n+1,0);
        for(auto&i:edges){
            g[i[0]].push_back(i[1]);
            g[i[1]].push_back(i[0]);
        } 
        int res=f(0,-1);
        f1(0,-1);
        return time;
    }
};
