class Solution {
public:
    vector<int> timeTaken(vector<vector<int>>& edges) {
        int N = edges.size() + 1;
        vvi g(N);
        for (auto &e: edges) {
            int u = e[0], v = e[1];
            g[u].pb(v), g[v].pb(u);
        }

        vi mx(N, 0);
        function<void(int, int)> dfs = [&](int u, int p) {
            for (auto v: g[u]) {
                if (v == p) continue;
                dfs(v, u);
                chmax(mx[u], mx[v] + (v % 2 == 0 ? 2 : 1));
            }  
        };
        dfs(0, -1);

        vi ans(N, 0);
        function<void(int, int, int)> dfs2 = [&](int u, int p, int pd) {
            ans[u] = max(mx[u], pd);
            int mx1 = pd, mx2 = 0;
            for (auto v: g[u]) {
                if (v == p) continue;
                int d = mx[v] + (v % 2 == 0 ? 2 : 1);
                if (d > mx1) mx2 = mx1, mx1 = d;
                else if (d > mx2) mx2 = d;
            }

            for (auto v: g[u]) {
                if (v == p) continue;
                int d = mx[v] + (v % 2 == 0 ? 2 : 1);
                int qd = d == mx1 ? mx2 : mx1;
                dfs2(v, u, qd + (u % 2 == 0 ? 2 : 1));
            }
        };
        dfs2(0, -1, 0);

        return ans;
    }
};
