class Solution(object):
    def timeTaken(self, edges):
        """
        :type edges: List[List[int]]
        :rtype: List[int]
        """
        n=len(edges)+1
        edge=[[] for i in range(n)]
        for i in edges:
            edge[i[0]].append(i[1])
            edge[i[1]].append(i[0])
        check=[0]*n
        time=[{} for i in range(n)]
        first=[0]*n
        second=[0]*n

        def ahead(root):
            check[root]=1
            t=0
            for i in edge[root]:
                if check[i]==0:
                    temp=ahead(i)
                    if i%2:
                        temp+=1
                    else:
                        temp+=2
                    t=max(t,temp)
                    time[root][i]=temp
                    if temp>first[root]:
                        second[root]=first[root]
                        first[root]=temp
                    else:
                        second[root]=max(second[root],temp)
            return t

        ahead(0)
        re=[0]*n
        check=[0]*n

        def back(root,other):
            check[root]=1
            re[root]=max(other,first[root])
            if root%2:
                for i in edge[root]:
                    if check[i]==0:
                        if time[root][i]==first[root]:
                            back(i,max(other,second[root])+1)
                        else:
                            back(i,re[root]+1)
            else:
                for i in edge[root]:
                    if check[i]==0:
                        if time[root][i]==first[root]:
                            back(i,max(other,second[root])+2)
                        else:
                            back(i,re[root]+2)

        back(0,0)
        return re
