class Solution {
public:
    int a,b,maxi;
    void dfs(int node, int prt, vector<vector<int>> &adj, int dist){
        if(dist > maxi){
            maxi = dist;
            a = node;
        }
        for(auto &i : adj[node]) if(i != prt) dfs(i, node, adj, dist+1+(i%2==0));
    }

    void dfs2(int node, int prt, vector<int> &arr, vector<vector<int>> &adj, int dist){
        arr[node] = dist;
        for(auto &i : adj[node]){
            if(i != prt) dfs2(i, node, arr, adj, dist+1+(node%2==0));
        }
    }

    vector<int> timeTaken(vector<vector<int>>& edges) {
        int n = edges.size()+1;
        vector<vector<int>> adj(n);
        for(auto &i : edges){
            adj[i[0]].push_back(i[1]);
            adj[i[1]].push_back(i[0]);
        }

        a = -1, maxi = -1;
        dfs(0,-1,adj,0);
        b = a, a = -1, maxi = -1;
        dfs(b,-1,adj,0);

        vector<int> da(n), db(n);
        dfs2(a, -1, da, adj, 0);
        dfs2(b, -1, db, adj, 0);

        vector<int> ans(n);
        for(int i = 0; i < n; i++){
            ans[i] = max(da[i], db[i]);
        }
        return ans;
    }
};
