class Solution:
    def timeTaken(self, edges: List[List[int]]) -> List[int]:
        n=len(edges)+1
        graph=[[]  for i in range(n)]
        for a,b in edges:
            graph[a].append(b)
            graph[b].append(a)
        height=[0]*(n)
        visit=[0]*(n)
        rls=[0]*(n)
        def dfs(node):
            sonls=[]
            visit[node]=1
            for son in graph[node]:
                if not visit[son]:
                    sonls.append(son)
            if not sonls:
                height[node]=0
                return 0
            curm=0
            for son in sonls:
                r=dfs(son)
                if son%2:
                    r+=1
                else:
                    r+=2
                curm=max(r,curm)
            height[node]=curm
            return curm
        dfs(0)
        visit=[0]*(n)
        print(height)
