            sonls=[]
            visit[node]=1
            for son in graph[node]:
                if not visit[son]:
                    sonls.append(son)
            if not sonls:
                rls[node]=curmaxlen
                return 0
            rls[node]=max(curmaxlen,height[node])
            ls=[]
            for son in sonls:
                if son%2:
                    tos=1
                else:
                    tos=2
                ls.append(height[son]+tos)
            ls.sort()
            m1=ls[-1]
            if len(ls)>1:
                m2=ls[-2]
            else:
                m2=0
            for son in sonls:
                if son%2:
                    tos=1
