class Solution {
public:
    vector<int> timeTaken(vector<vector<int>>& edges) {
        int n = edges.size() + 1;
        vector<int> ord(n), par(n, -1), f(n), g(n), h(n);
        vector<vector<int> > e(n);
        for(auto &it: edges) {
            e[it[0]].push_back(it[1]);
            e[it[1]].push_back(it[0]);
        }
        ord[0] = 0;
        for(int i = 0, tot = 1; i < tot; ++i) {
            int u = ord[i];
            h[u] = u;
            for(int v: e[u]) {
                if(v == par[u])
                    continue;
                par[v] = u;
                ord[tot++] = v;
            }
        }
        for(int i = n - 1; i > 0; --i) {
            int u = ord[i], p = par[u], tp = f[u] + 2 - (u & 1);
            if(tp > f[p]) {
                g[p] = f[p];
                f[p] = tp;
                h[p] = u;
            } else if(tp > g[p]) {
                g[p] = tp;
            }
        }
        for(int i = 1; i < n; ++i) {
            int u = ord[i], p = par[u], tp = (h[p] == u ? g[p] : f[p]) + 2 - (p & 1);
            if(tp > f[u]) {
                g[u] = f[u];
                f[u] = tp;
                h[u] = p;
            } else if(tp > g[u]) {
                g[u] = tp;
            }
        }
        return f;
    }
};
