class Solution {
public:
    void dfs(int u , int par , vector<vector<int>> &g , vector<int> &val , vector<int> &dis){
        for(int v:g[u]){
            if(v==par)continue;
            dis[v]=dis[u]+val[v];
            dfs(v,u,g,val,dis);
        }
    }
    vector<int> timeTaken(vector<vector<int>>& edges) {
        int n=edges.size()+1;
        vector<int>val(n);
        vector<vector<int>>g(n);
        for(vector<int> v :edges){
            g[v[0]].push_back(v[1]);
            g[v[1]].push_back(v[0]);
        }
        for(int i=0;i<n;i++){
            if(i%2==0)val[i]=2;
            else val[i]=1;
        }
        vector<int>dis(n);
        dis[0]=val[0];
        dfs(0,-1,g,val,dis);
        int mx=-1,node1;
        for(int i=0;i<n;i++){
            if(dis[i]>mx){
                mx=dis[i];
                node1=i;
            }
        }
        vector<int>dis1(n);
        dis1[node1]=val[node1];
        dfs(node1,-1,g,val,dis1);
        mx=-1;int node2;
        for(int i=0;i<n;i++){
            if(dis1[i]>mx){
                mx=dis1[i];
                node2=i;
            }
        }
        vector<int>dis2(n);
        dis2[node2]=val[node2];
        dfs(node2,-1,g,val,dis2);
        // cout<<node1<<" "<<node2<<endl;
        vector<int>ans(n);
        for(int i=0;i<n;i++){
            int p=max(dis1[i],dis2[i]);
            // cout<<i<<" "<<dis1[i]<<" "<<dis2[i]<<endl;
            p-=val[i];
            ans[i]=p;
        }
        return ans;
    }
};
