class Solution {
public:
    using ll=long long;
    long long maximumValueSum(vector<vector<int>>& v) {
        int n=v.size();
        int m=v[0].size();
        vector<vector<pair<ll,ll>>>vv(n);
        for(ll i=0;i<n;i++){
        set<pair<ll,ll>>va;
            for(int j=0;j<m;j++){
                va.insert({v[i][j],j});
                if(va.size()>3)va.erase(va.begin());
            }
            for(auto j:va)vv[i].push_back(j);
        }
        // for(ll i=0;i<n;i++){
        //     for(auto j:va[i]){
        //         vv[i].push_back(j);
        //     }
        // }
        ll mx=-1e18;
        for(ll i=0;i<n;i++){
            for(ll j=i+1;j<n;j++){
                for(ll k=j+1;k<n;k++){
                    for(ll l=0;l<3;l++){
                        for(ll mm=0;mm<3;mm++){
                            for(ll nn=0;nn<3;nn++){
                                if(vv[i][l].second!=vv[j][mm].second&&vv[j][mm].second!=vv[k][nn].second&&vv[k][nn].second!=vv[i][l].second){
                                    mx=max(mx,vv[i][l].first+vv[j][mm].first+vv[k][nn].first);
                                }
                            }
                        }
                    }
                }
            }
        }
        return mx;
    }
};
