class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        auto &b = board;
        int m=b.size(), n=b[0].size();
        if(m>n){
            vector<vector<int>> a = rotate(b);
            return maximumValueSum(a);
        }
        vector<vector<vector<long long>>> bvi(m);
        priority_queue<vector<long long>, vector<vector<long long>>, greater<vector<long long>>> pqAll;
        for(int i=0; i<m; i++){
            priority_queue<vector<long long>, vector<vector<long long>>, greater<vector<long long>>> pqCur;
            for(int j=0; j<n; j++) bvi[i].push_back({b[i][j], j});
            sort(bvi[i].begin(), bvi[i].end(), greater<vector<long long>>());
            for(int j=0; j<3; j++){
                pqCur.push({bvi[i][j][0], bvi[i][j][1], (long long)i});
                while(pqCur.size()>3) pqCur.pop();
            }
            while(!pqCur.empty()){
                pqAll.push(pqCur.top());
                pqCur.pop();
                // while(pqAll.size()>90) pqAll.pop();
            }
        }
        vector<vector<long long>> arr;
        while(!pqAll.empty()){
            arr.push_back(pqAll.top());
            pqAll.pop();
        }
        long long res=-1e10;
        for(int i=arr.size()-1; i>=0; i--){
            for(int j=i-1; j>=0; j--){
                if(arr[i][1]==arr[j][1] || arr[i][2]==arr[j][2]) continue;
                for(int k=j-1; k>=0; k--){
                    if(arr[i][1]==arr[k][1] || arr[i][2]==arr[k][2]) continue;
                    if(arr[j][1]==arr[k][1] || arr[j][2]==arr[k][2]) continue;
                    res = max(res, arr[i][0]+arr[j][0]+arr[k][0]);
                }
            }
        }
        return res;
    }
    vector<vector<int>> rotate(vector<vector<int>> &a){
        int m = a.size();
        int n = a[0].size();
        vector<vector<int>> b(n, vector<int>(m, 0));
        for(int i=0; i<m; i++){
            for(int j=0; j<n; j++){
                b[j][i] = a[i][j];
            }
        }
        return b;
    }
};
