#include <iostream>
#include <vector>
#include <algorithm>
#include <limits.h>

using namespace std;

class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int m = board.size();
        int n = board[0].size();
        vector<vector<pair<long long, int>>> rows(m);

        for (int i = 0; i < m; i++) {
            rows[i].push_back({LLONG_MIN, -1});
            rows[i].push_back({LLONG_MIN, -1});
            rows[i].push_back({LLONG_MIN, -1});

            for (int j = 0; j < n; j++) {
                if (board[i][j] > rows[i][0].first) {
                    rows[i][2] = rows[i][1];
                    rows[i][1] = rows[i][0];
                    rows[i][0] = {(long long)board[i][j], j};
                } else if (board[i][j] > rows[i][1].first) {
                    rows[i][2] = rows[i][1];
                    rows[i][1] = {(long long)board[i][j], j};
                } else if (board[i][j] > rows[i][2].first) {
                    rows[i][2] = {(long long)board[i][j], j};
                }
            }
        }

        long long res = LLONG_MIN;

        for (int first = 0; first < m; first++) {
            for (auto& first_pair : rows[first]) {
                for (int second = 0; second < m; second++) {
                    if (second == first) continue;
                    for (auto& second_pair : rows[second]) {
                        if (second_pair.second == first_pair.second) continue;
                        for (int third = 0; third < m; third++) {
                            if (third == first || third == second) continue;
                            for (auto& third_pair : rows[third]) {
                                if (third_pair.second == first_pair.second || third_pair.second == second_pair.second) continue;
                                res = max(res, first_pair.first + second_pair.first + third_pair.first);
                            }
                        }
                    }
                }
            }
        }

        return res;
    }
};
