class Solution {
public:
    // classsic 3D dp

    long long maximumValueSum(vector<vector<int>>& board) {
        int n = board.size();
        int m = board[0].size();
        vector<pair<int,pair<int,int>>> ss;
        for (int i=0;i<n;i++) {
            for (int j=0;j<m;j++) {
                ss.push_back(make_pair(board[i][j],make_pair(i,j)));
            }
        }
        sort(ss.begin(),ss.end());
        reverse(ss.begin(),ss.end());
        long long ans = -1e18;
        long long sz = ss.size();
        for (int i=0;i<min(sz,200LL);i++) {
            for (int j=0;j<min(sz,200LL);j++) {
                for (int k=0;k<min(sz,200LL);k++) {
                    pair<int,int> p1 = make_pair(ss[i].second.first, ss[i].second.second);
                    pair<int,int> p2 = make_pair(ss[j].second.first, ss[j].second.second);
                    pair<int,int> p3 = make_pair(ss[k].second.first, ss[k].second.second);
                    if (p1.first!=p2.first && p1.first!=p3.first && p2.first!=p3.first && p1.second!=p2.second && p1.second!=p3.second && p2.second!=p3.second) {
                        long long a1 = ss[i].first;
                        long long a2 = ss[j].first;
                        long long a3 = ss[k].first;
                        ans = max(ans, a1+a2+a3);
                    }
                }
            }
        }
        return ans;
    }
};
