function maximumValueSum(board: number[][]): number {
  const m = board.length;
  const n = board[0].length;

  const a: number[][] = [];
  for (var i = 0; i < m; i++) {
    const ai: number[] = new Array(n).fill(0).map((_, i) => i);
    ai.sort((u, v) => (board[i][u] > board[i][v] ? -1 : 1));
    a.push(ai.slice(0, 3));
  }

  var res = -1000000000 * 3;
  for (var i1 = 0; i1 < m - 2; i1++) {
    for (var i2 = i1 + 1; i2 < m - 1; i2++) {
      for (var i3 = i2 + 1; i3 < m; i3++) {
        for (var j1 = 0; j1 < 3; j1++) {
          for (var j2 = 0; j2 < 3; j2++) {
            for (var j3 = 0; j3 < 3; j3++) {
              if (a[i1][j1] === a[i2][j2] || a[i1][j1] === a[i3][j3] || a[i2][j2] === a[i3][j3]) {
                continue;
              }
              res = Math.max(res, board[i1][a[i1][j1]] + board[i2][a[i2][j2]] + board[i3][a[i3][j3]]);
            }
          }
        }
      }
    }
  }
  return res;
}
