class Solution:
    def maximumValueSum(self, board: List[List[int]]) -> int:
        n,m=len(board),len(board[0])
        x=[]
        for i in range(n):
            a=[(board[i][j],j) for j in range(m)]
            a.sort(reverse=True)
            x.append(a[0:3])
        @cache
        def dfs(i,mask):
            if mask.bit_count()==3:
                return 0
            if i>=n:
                return -inf
            res=dfs(i+1,mask)
            for j in range(3):
                 if ((mask>>x[i][j][1])&1)==0:
                        res=max(res,dfs(i+1,mask|(1<<x[i][j][1]))+x[i][j][0])
            return res
        ans=dfs(0,0)
        dfs.cache_clear()
        return ans
