class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {

        vector<vector<pair<long long int,long long int>>> g1;

        for(int x=0;x<board.size();x++){
            vector<pair<long long int,long long int>> g3;
            for(int y=0;y<board[0].size();y++){
                g3.push_back({board[x][y],y});



            }
            sort(g3.begin(),g3.end());


            g1.push_back(g3);

        }
        long long int ans=-100000000000000000;
        int m=board[0].size();
        for(int x=0;x<board.size();x++){
            for(int y=x+1;y<board.size();y++){
                for(int z=y+1;z<board.size();z++){

                    for(long long int x1=m-1;x1>=m-3;x1--){


                  for(long long int y1=m-1;y1>=m-3;y1--){


              for(long long int z1=m-1;z1>=m-3;z1--){


                if(g1[x][x1].second!=g1[y][y1].second&&g1[z][z1].second!=g1[y][y1].second&&g1[z][z1].second!=g1[x][x1].second){
                    ans=max(ans,g1[x][x1].first+g1[y][y1].first+g1[z][z1].first);
                }


                    }

                    }


                    }
                }
            }
        }
        return ans;
    }
};
