class Solution {
public:

    long long maximumValueSum(vector<vector<int>>& board) {
        int n = board.size(), m = board[0].size();
        vector<vector<pair<long long, pair<long long, long long>>>> grid;
        for (int i=0; i<n; i++)
        {
            vector<pair<long long, pair<long long, long long>>> temp, curr;
            for (int j=0; j<m; j++)
            {
                temp.push_back({board[i][j], {i, j}});
            }
            sort(temp.begin(), temp.end());
            reverse(temp.begin(), temp.end());
            for (int i=0; i<3; i++) curr.push_back(temp[i]);
            grid.push_back(curr);
        }

        long long ans = -1e17;
        for (int i=0; i<grid.size(); i++)
        {
            for (int j=0; j<grid.size(); j++)
            {
                if (i==j) continue;
                for (int k=0; k<grid.size(); k++)
                {
                    if (j==k || i==k) continue;
                    for (int p=0; p<3; p++)
                    {
                        for (int q=0; q<3; q++)
                        {
                            for (int r=0; r<3; r++)
                            {
                                int x1 = grid[i][p].second.first, y1 = grid[i][p].second.second;
                                int x2 = grid[j][q].second.first, y2 = grid[j][q].second.second;
                                int x3 = grid[k][r].second.first, y3 = grid[k][r].second.second;
                                if (x1==x2 || x1==x3 || x2==x3 || y1==y2 || y2==y3 || y3==y1) continue;
                                ans = max(ans, grid[i][p].first + grid[j][q].first + grid[k][r].first);
                            }
                        }
                    }
                }
            }

        }
        return ans;
        return 0;
    }
};
