type Element struct {
	value int
	row   int
	col   int
}
type MaxHeap []Element

func (h MaxHeap) Len() int           { return len(h) }
func (h MaxHeap) Less(i, j int) bool { return h[i].value > h[j].value }
func (h MaxHeap) Swap(i, j int)      { h[i], h[j] = h[j], h[i] }

func (h *MaxHeap) Push(x interface{}) {
	*h = append(*h, x.(Element))
}

func (h *MaxHeap) Pop() interface{} {
	old := *h
	n := len(old)
	x := old[n-1]
	*h = old[0 : n-1]
	return x
}

func maximumValueSum(board [][]int) int64 {
	m := len(board)
	if m == 0 {
		return 0
	}
	n := len(board[0])

	rowTop := make([][]Element, m)
	colTop := make([][]Element, n)

	for i := 0; i < m; i++ {
		rowTop[i] = make([]Element, 0, n)
		for j := 0; j < n; j++ {
			rowTop[i] = append(rowTop[i], Element{value: board[i][j], row: i, col: j})
		}
		sort.Sort(MaxHeap(rowTop[i]))
		if len(rowTop[i]) > 3 {
			rowTop[i] = rowTop[i][:3]
		}
	}

	for j := 0; j < n; j++ {
		colTop[j] = make([]Element, 0, m)
		for i := 0; i < m; i++ {
			colTop[j] = append(colTop[j], Element{value: board[i][j], row: i, col: j})
		}
		sort.Sort(MaxHeap(colTop[j]))
		if len(colTop[j]) > 3 {
			colTop[j] = colTop[j][:3]
		}
	}

	var maxSum, sum int64
	maxSum = -999999999999
	for i := 0; i < m; i++ {
		for j := 0; j < len(rowTop[i]); j++ {
			elem1 := rowTop[i][j]
			for k := 0; k < n; k++ {
				if k == elem1.col {
					continue
				}
				for l := 0; l < len(colTop[k]); l++ {
					elem2 := colTop[k][l]
					if elem2.row == elem1.row {
						continue
					}
					for p := 0; p < n; p++ {
						if p == elem1.col || p == elem2.col {
							continue
						}
						for q := 0; q < len(colTop[p]); q++ {
							elem3 := colTop[p][q]
							if elem3.row == elem1.row || elem3.row == elem2.row {
								continue
							}
							sum = int64(elem1.value + elem2.value + elem3.value)
							if sum > maxSum {
								maxSum = sum
							}
						}
					}
				}
			}
		}
	}

	return maxSum
}
