    long long maximumValueSum(vector<vector<int>>& board) {
        unordered_map<ll,vector<pair<ll,ll>>> mapp;
        int n = board.size();
        int m = board[0].size();
        for(int j = 0;j<m;j++){
            pair<ll,ll> f = {-1e18,0};
            pair<ll,ll> s = {-1e18,0};
            pair<ll,ll> t = {-1e18,0};
            for(int i = 0;i<n;i++){
                ll val = board[i][j];
                if(val > f.first){
                    //shift
                    t = s;
                    s = f;
                    f = {val,i};
                }
                else if(val > s.first){
                    t = s;
                    s = {val,i};
                }
                else if(val > t.first){
                    t = {val,i};
                }
            }
            mapp[j].push_back(f);
            mapp[j].push_back(s);
            mapp[j].push_back(t);
        }
        //done 
        ll ans = -1e18;
        for(int i = 0;i<m-2;i++){
            for(int j = i+1;j<m-1;j++){
                for(int k = j+1;k<m;k++){
                    vector<pair<ll,ll>> vec1 = mapp[i];
                    vector<pair<ll,ll>> vec2 = mapp[j];
                    vector<pair<ll,ll>> vec3 = mapp[k];
                    for(int a = 0;a<3;a++){
                        for(int b = 0;b<3;b++){
                            for(int c = 0;c<3;c++){
                                ll A = vec1[a].first;
                                ll B = vec2[b].first;
                                ll C = vec3[c].first;
                                int x1 = vec1[a].second;
                                int x2 = vec2[b].second;
                                int x3 = vec3[c].second;
                                if(x1 != x2 && x1 != x3 && x2 != x3){
                                    // cout<<A<<" "<<B<<" "<<C<<" "<<endl;
                                    // cout<<x1<<" "<<x2<<" "<<x3<<" "<<endl;
                                    ans = max(ans,A+B+C);
                                }
                            }
                        }
                    }
                }
            }
        }
        // for(int j = 0;j<m;j++){
        //     cout<<j<<" -> "<<mapp[j][0].first<<" "<<mapp[j][1].first<<" "<<mapp[j][2].first<<endl;
        //     cout<<j<<" -> "<<mapp[j][0].second<<" "<<mapp[j][1].second<<" "<<mapp[j][2].second<<endl;
