class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int n = board.size(), m = board[0].size();
        vector<vector<int>> idx(n, vector<int>(m));
        for(int i = 0; i < n; ++i) {
            for(int j = 0; j < m; ++j) {
                idx[i][j] = j;
            }
            sort(idx[i].begin(), idx[i].end(), [&](int a, int b) {
                return board[i][a] > board[i][b];
            });
            idx[i].resize(3);
        }
        long long ans = -1e18;
        for(int i = 0; i < n; ++i) {
            for(int p = 0; p < 3; ++p) {
                for(int j = i + 1; j < n; ++j) {
                    for(int q = 0; q < 3; ++q) {
                        if(idx[i][p] == idx[j][q]) continue;
                        for(int k = j + 1; k < n; ++k) {
                            for(int r = 0; r < 3; ++r) {
                                if(idx[i][p] == idx[k][r] || idx[j][q] == idx[k][r]) continue;
                                ans = max(ans, 1LL * board[i][idx[i][p]] + board[j][idx[j][q]] + board[k][idx[k][r]]);
                            }
                        }
                    }
                }
            }
        }
        return ans;
    }
};
