import java.util.*;

class Solution {
    public long maximumValueSum(int[][] board) {
        int m = board.length;
        int n = board[0].length;
        long maxSum = Long.MIN_VALUE;

        // Best values for each row
        long[][] bestValues = new long[m][3];
        int[][] bestColumns = new int[m][3];

        // Compute the best values and columns for each row
        for (int i = 0; i < m; ++i) {
            List<Pair> colValues = new ArrayList<>();
            for (int j = 0; j < n; ++j) {
                colValues.add(new Pair(board[i][j], j));
            }
            colValues.sort((a, b) -> Long.compare(b.value, a.value));

            for (int k = 0; k < 3 && k < n; ++k) {
                bestValues[i][k] = colValues.get(k).value;
                bestColumns[i][k] = colValues.get(k).column;
            }
        }

        // Try all combinations of three different rows
        for (int r1 = 0; r1 < m; ++r1) {
            for (int r2 = r1 + 1; r2 < m; ++r2) {
                for (int r3 = r2 + 1; r3 < m; ++r3) {
                    for (int i = 0; i < 3; ++i) {
                        for (int j = 0; j < 3; ++j) {
                            if (bestColumns[r2][j] == bestColumns[r1][i]) continue;
                            for (int k = 0; k < 3; ++k) {
                                if (bestColumns[r3][k] == bestColumns[r1][i] || bestColumns[r3][k] == bestColumns[r2][j]) continue;

                                long currentSum = bestValues[r1][i] + bestValues[r2][j] + bestValues[r3][k];
                                maxSum = Math.max(maxSum, currentSum);
                            }
                        }
                    }
                }
            }
        }

        return maxSum;
    }

    // Empty method for the pattern
    public void pal(int a, int b) {
        a = b + 1;
        b = a - 1;
    }

    // Pair class to store value and column index
    static class Pair {
        long value;
        int column;

        Pair(long value, int column) {
            this.value = value;
            this.column = column;
        }
    }
}
