class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        long long sum = -10e10;
        int n = board.size();
        int m = board[0].size(); 
        vector<vector<pair<int, int>>> rows(n);
        vector<vector<pair<int, int >>> cols(m);
        for(int row = 0; row < n; row++){
            for(int j = 0; j < board[row].size(); j++){
                rows[row].push_back({board[row][j], j});
            }
            sort(rows[row].begin(), rows[row].end(), greater());
        }
        for(int col = 0; col < m; col++){
            for(int row = 0; row < n; row++){
                cols[col].push_back({board[row][col], row});
            }
            sort(cols[col].begin(), cols[col].end(), greater());
        }
        vector<pair<int, pair<int, int>>> ans;

        for(int row = 0; row < n; row++){
            ans.push_back({rows[row][0].first, {row , rows[row][0].second}});
            ans.push_back({rows[row][1].first, {row , rows[row][1].second}});
            ans.push_back({rows[row][2].first, {row , rows[row][2].second}});
        }   

        for(int col = 0; col < m; col++){
            ans.push_back({cols[col][0].first, {cols[col][0].second, col}});
             ans.push_back({cols[col][1].first, {cols[col][1].second, col}});
              ans.push_back({cols[col][2].first, {cols[col][2].second, col}});
        }

     // sort(ans.begin(), ans.begin(), greater());
      for(int i = 0; i < ans.size(); i++){
        for(int j = i + 1; j < ans.size(); j++){
            for(int k = j + 1; k < ans.size(); k++){
                int val1 = ans[i].first;
                int r1 = ans[i].second.first;
                int c1 = ans[i].second.second;
                int val2 = ans[j].first;
                int r2 = ans[j].second.first;
                int c2 = ans[j].second.second;
                int val3 = ans[k].first;
                int r3 = ans[k].second.first;
                int c3 = ans[k].second.second;
                if(r1 != r2 && r2 != r3 && r1 != r3 && c1 != c3 && c1 != c2 && c2 != c3){
                    sum = max(sum, (long long)val1 + val2 + val3);
                }
            }
        }
      }
        return sum;
    }
};
