class Solution {
    int m, n;
    int[][] board;
    public long maximumValueSum(int[][] board) {
        this.m = board.length;
        this.n = board[0].length;
        this.board = board;
        int[][] g = new int[m][3];

        for(int i=0;i<m;i++){
            g[i] = findMaxThree(board[i]);
        }
        int[] selN = new int[3];
        long res = dfs(g, 0l, 0, 0, selN);
        return res;
    }

    long dfs(int[][] g, long res, int cnt, int curM, int[] selN){
        if(cnt==3){
            return res;
        }
        if(curM>=m) return ((long)Integer.MIN_VALUE)*3;

        long ans = dfs(g, res, cnt, curM+1, selN);
        for(int p: g[curM]){
            boolean isContain = false;
            for(int i=0;i<cnt;i++){
                if(selN[i]==p){
                    isContain = true;
                    break;
                }
            }
            if(isContain) continue;
            selN[cnt] = p;
            // System.out.println("p:"+p+", curM:"+curM);
            long tmp = dfs(g, res+board[curM][p], cnt+1, curM+1, selN);
            ans = Math.max(tmp, ans);
        }
        return ans;
    }

    int[] findMaxThree(int[] arr){
        int[] ans = new int[]{0, -1, -1};
        for(int i=0;i<n;i++){
            int p = ans[0];
            if(arr[i]>arr[p]){
                ans[0] = i;
            }
        }
        for(int i=0;i<n;i++){
            if(i==ans[0])continue;
            int p = ans[1];
            if(p==-1) {
                p = i;
                ans[1] = i;
            }
            if(arr[i]>arr[p]){
                ans[1] = i;
            }
        }

        for(int i=0;i<n;i++){
            if(i==ans[0]||i==ans[1])continue;
            int p = ans[2];
            if(p==-1) {
                p = i;
                ans[2] = i;
            }
            if(arr[i]>arr[p]){
                ans[2] = i;
            }
        }
        // System.out.println("ans0:"+ans[0]+", ans1:"+ans[1]+", ans2:"+ans[2]);
        return ans;
    }
}
