#include <iostream>
#include <vector>
#include <algorithm>
#include <climits>
#include <set>

#define ll long long int

using namespace std;

class Solution {
public:
    ll maximumValueSum(vector<vector<int>>& board) {
        ll row = board.size();
        ll col = board[0].size();
        ll n = col;

        vector<vector<pair<ll, ll>>> cal(n);
        for (ll i = 0; i < col; ++i) {
            vector<pair<ll, ll>> temp;
            for (ll j = 0; j < row; j++) {
                temp.push_back({board[j][i], j});
            }
            sort(temp.rbegin(), temp.rend());  
            for (ll k = 0; k < min(3LL, (ll)temp.size()); ++k) {
                cal[i].push_back(temp[k]);
            }
        }

        ll maxSum = LLONG_MIN;

        for (ll i = 0; i < n; ++i) {
            for (ll j = i + 1; j < n; ++j) {
                for (ll k = j + 1; k < n; ++k) {
                    for (const auto& pi : cal[i]) {
                        for (const auto& pj : cal[j]) {
                            for (const auto& pk : cal[k]) {
                                if (pi.second != pj.second && pi.second != pk.second && pj.second != pk.second) {
                                    ll sum = pi.first + pj.first + pk.first;
                                    maxSum = max(maxSum, sum);
                                }
                            }
                        }
                    }
                }
            }
        }

        return maxSum;
    }
};
