class Solution {
public:
    const long long INF=1e12;
    long long maximumValueSum(vector<vector<int>>& board) {
        int n=board.size();
        int m=board[0].size();
        vector<vector<pair<long long,long long>>> a(n,vector<pair<long long,long long>> (m));
        for(long long i=0;i<n;++i)
        {
            for(long long j=0;j<m;++j)
            {
                a[i][j]={(long long)board[i][j],j};
            }
            sort(a[i].begin(),a[i].end());
        }
        long long ans=-INF;
        for(long long i=0;i<n;++i)
        {
            for(long long j=i+1;j<n;++j)
            {
                for(long long k=j+1;k<n;++k)
                {
//                     int temp=a[i][m-1].first;
//                     set<int> indexes;
//                     indexes.insert(a[i][m-1].second);
//                     if(a[i][m-1].second==a[j][m-1].second)
//                     {
//                         indexes.insert(a[j][m-2].second);
//                         temp+=a[j][m-2].first;
//                     }
//                     else 
//                     {
//                         indexes.insert(a[j][m-1].second);
//                         temp+=a[j][m-1].first;
//                     }
//                     int ed=m-1;
//                     while(indexes.find(a[k][ed].second)!=indexes.end())ed--;
                    for(long long one=m-1;one>=m-3;--one)
                    {
                        for(long long two=m-1;two>=m-3;--two)
                        {
                            if(a[i][one].second==a[j][two].second)continue;
                            for(long long third=m-1;third>=m-3;--third)
                            {
                               if(a[k][third].second==a[i][one].second || a[k][third].second==a[j][two].second)continue;
                                ans=max(ans,a[i][one].first+a[j][two].first+a[k][third].first);
                            }
                        }
                    }
                }
            }
        }
        return ans;
    }
};
