class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        long long n = board.size();
        long long m = board[0].size();
        set<pair<int, int>> st[n];
        long long ans = -1e18;

        for(long long i = 0; i < n; i++)
        {
            for(long long j = 0; j < m; j++)
            {
                st[i].insert({board[i][j], j});
            }
        }

        for(long long i = 0; i < n; i++)
        {
            for(long long j = i+1; j < n; j++)
            {
                for(long long k = j+1; k < n; k++)
                {
                    vector<array<long long, 2>> a, b, c;
                    long long cnt = 0;
                    for(auto it = st[i].rbegin(); it != st[i].rend(); it++)
                    {
                        if(cnt==3) break;
                        a.push_back({it->first, it->second});
                        cnt++;
                    }
                    cnt = 0;
                    for(auto it = st[j].rbegin(); it != st[j].rend(); it++)
                    {
                        if(cnt==3) break;
                        b.push_back({it->first, it->second});
                        cnt++;
                    }
                    cnt = 0;
                    for(auto it = st[k].rbegin(); it != st[k].rend(); it++)
                    {
                        if(cnt==3) break;
                        c.push_back({it->first, it->second});
                        cnt++;
                    }

                    long long sum = 0;

                    for(long long p = 0; p < 3; p++)
                    {
                        for(long long q = 0; q < 3; q++)
                        {
                            for(long long r = 0; r < 3; r++)
                            {
                                if(a[p][1]==b[q][1] || b[q][1]==c[r][1] || a[p][1]==c[r][1]) continue;
                                // cout << a[p][0] << " " << b[q][0] << " " << c[r][0] <<'\n';
                                sum = a[p][0]+b[q][0]+c[r][0];
                                ans = max(ans, sum);
                            }
                        }
                    }
                }
            }
        }
        return ans;
    }
};
