class Solution:
    def maximumValueSum(self, board: List[List[int]]) -> int:
        nums = [sorted(list(range(len(i))), key = lambda x: -i[x])[:3] for i in board]
        @cache
        def dfs(cur, k, state):
            if k == 0: return 0
            res = -inf
            for i in range(cur, len(board)):
                for j in range(3):
                    if state >> nums[i][j] & 1 == 0:
                        res = max(res, dfs(i+1, k-1, state | 1 << nums[i][j]) + board[i][nums[i][j]])
            return res
        return dfs(0, 3, 1 << len(board[0]))
