class Solution {
    public long maximumValueSum(int[][] board) {
        int m = board.length;
        int[][] stats = new int[m][];
        for (int i = 0; i < m; ++i) {
            stats[i] = firstThree(board[i]);
        }
        long max = Long.MIN_VALUE;
        for (int i = 0; i < m; ++i) for (int pi : stats[i]) {
            long a = board[i][pi];
            for (int j = 0; j < m; ++j) {
                if (j == i) continue;
                for (int pj : stats[j]) {
                    if (pi == pj) continue;
                    long b = board[j][pj];
                    for (int k = 0; k < m; ++k) {
                        if (k == i || k == j) continue;
                        for (int pk : stats[k]) {
                            if (pk == pi || pk == pj) continue;
                            long c = board[k][pk];
                            max = Math.max(max, a + b + c);
                        }
                    }
                }
            }

        }
        return max;
    }

    int[] firstThree(int[] row) {
        int max1, max2, max3;
        int i1 = 0, i2 = 0, i3 = 0;
        max1 = max2 = max3 = Integer.MIN_VALUE;
        for (int i = 0; i < row.length; ++i) {
            int num = row[i];
            if (num > max1) {
                max3 = max2; i3 = i2;
                max2 = max1; i2 = i1;
                max1 = num; i1 = i;
            } else if (num > max2) {
                max3 = max2; i3 = i2;
                max2 = num; i2 = i;
            } else if (num > max3) {
                max3 = num; i3 = i;
            }
        }
        return new int[] {i1, i2, i3};
    }
}
