long long maximumValueSum(int** board, int boardSize, int* boardColSize) {
    int row_max[boardSize][3];
    int col_index[boardSize][3];
    const int NINF = -2147483648;
    for(int i = 0; i < boardSize; i++){
        row_max[i][0] = NINF;
        row_max[i][1] = NINF;
        row_max[i][2] = NINF;
        col_index[i][0] = -1;
        col_index[i][1] = -1;
        col_index[i][2] = -1;
    }
    for(int i = 0; i < boardSize; i++){
        for(int j = 0; j < boardColSize[i]; j++){
            if(board[i][j] > row_max[i][0]){
                row_max[i][2] = row_max[i][1];
                row_max[i][1] = row_max[i][0];
                row_max[i][0] = board[i][j];
                col_index[i][2] = col_index[i][1];
                col_index[i][1] = col_index[i][0];
                col_index[i][0] = j;
            }
            else if(board[i][j] > row_max[i][1]){
                row_max[i][2] = row_max[i][1];
                row_max[i][1] = board[i][j];
                col_index[i][2] = col_index[i][1];
                col_index[i][1] = j;
            }
            else if(board[i][j] > row_max[i][2]){
                row_max[i][2] = board[i][j];
                col_index[i][2] = j;
            }
        }
    }
    long long max = -9223372036854775808;
    //long long max = NINF;
    for(int i = 0; i < boardSize; i++){
        long long sum = 0;
        int used1 = -1;
        int used2 = -1;
        for(int c1 = 0; c1 < 3; c1++){
            sum += (long long)row_max[i][c1];
            used1 = col_index[i][c1];
            for(int j = i+1; j < boardSize; j++){
                for(int c2 = 0; c2 < 3; c2++){
                    if(col_index[j][c2] == used1){
                        continue;
                    }
                    sum += (long long)row_max[j][c2];
                    used2 = col_index[j][c2];
                    for(int k = j+1; k < boardSize; k++){
                        for(int c3 = 0; c3 < 3; c3++){
                            if(col_index[k][c3] == used1 || col_index[k][c3] == used2){
                                continue;
                            }
                            sum += (long long)row_max[k][c3];
                            if(sum > max){
                                max = sum;
                            }
                            sum -= (long long)row_max[k][c3];
                        }
                    }
                    sum -= (long long)row_max[j][c2];
                }
            }
            sum -= (long long)row_max[i][c1];
        }
    }
    return max;
}
