class Solution {
public:

    long long solve(vector<vector<pair<long long, long long>>>& v) {
        sort(v.begin(),v.end());
        int n = v.size();
        long long ans = LONG_MIN;

        for (int i1 = 0; i1 < n - 2; ++i1) {
            for (int x1 = 0; x1 < 3; ++x1) {
                long long first = v[i1][x1].first;
                long long second1 = v[i1][x1].second;

                vector<pair<long long, long long>> potential_pairs;
                for (int i2 = i1 + 1; i2 < n - 1; ++i2) {
                    for (int x2 = 0; x2 < 3; ++x2) {
                        long long second2 = v[i2][x2].second;
                        if (second2 == second1) continue;
                        long long second_value = v[i2][x2].first;
                        potential_pairs.push_back({second_value, second2});
                    }
                }

                sort(potential_pairs.rbegin(), potential_pairs.rend());

                long long max_second = LONG_MIN;
                for (const auto& p : potential_pairs) {
                    long long second_value = p.first;
                    long long second2 = p.second;
                    if (second2 == second1) continue;

                    bool found_valid_third = false;
                    for (int i3 = i1 + 2; i3 < n; ++i3) {
                        for (int x3 = 0; x3 < 3; ++x3) {
                            long long second3 = v[i3][x3].second;
                            if (second3 != second1 && second3 != second2) {
                                found_valid_third = true;
                                max_second = max(max_second, second_value + v[i3][x3].first);
                                // break;
                            }
                        }
                        // if (found_valid_third) break;
                    }
                }

                ans = max(ans, first + max_second);
            }
        }

        return ans;
}
