#include<bits/stdc++.h>
using namespace std;

class NodeInfo{
    public:
    long long val;
    int x;
    int y;

    NodeInfo(long long val, int x, int y){
        this->val = val;
        this->x = x;
        this->y = y;
    }
};

class Solution {
public:

    static bool comp(const NodeInfo &node1, const NodeInfo &node2){
      return node1.val>node2.val;
    }

    long long maximumValueSum(vector<vector<int>>& board) {
        int m = board.size(), n = board[0].size();

        vector<NodeInfo>nodes;

        for(int i = 0; i<m; i++){
            for(int j = 0; j<n; j++){
                NodeInfo node(board[i][j], i, j);

                nodes.push_back(node);

            }
        }

        sort(nodes.begin(), nodes.end(), comp);

        long long maxx = LONG_LONG_MIN;

        int ptr1 = -1, ptr2 = -1, ptr3 = -1;

        for(int i = 0; i<m*n; i++){
          ptr1 = i;

          for(int j = i+1; j<m*n; j++){
            if(nodes[j].x != nodes[ptr1].x && nodes[j].y != nodes[ptr1].y){
              ptr2 = j;

                for(int k = ptr2+1; k<m*n; k++){
                  if((nodes[k].x != nodes[ptr1].x && nodes[k].y != nodes[ptr1].y) && (nodes[k].x != nodes[ptr2].x && nodes[k].y != nodes[ptr2].y)){
                    ptr3 = k;
                    maxx = max(maxx, nodes[ptr1].val + nodes[ptr2].val + nodes[ptr3].val);
                    break;
                  }
                }


            }
          }


          // cout<<"ITER:\n";
          // cout<<nodes[ptr1].x<<' '<<nodes[ptr1].y<<'\n';
          // cout<<nodes[ptr2].x<<' '<<nodes[ptr2].y<<'\n';
          // cout<<nodes[ptr3].x<<' '<<nodes[ptr3].y<<'\n';

          // maxx = max(maxx, nodes[ptr1].val + nodes[ptr2].val + nodes[ptr3].val);

          ptr1 = -1;
          ptr2 = -1;
          ptr3 = -1;
        }

        return maxx;
    }
};
